fun<T: Drawable> T.show(sizeX: Double, sizeY: Double): Any {
    val writer = de.erichseifert.gral.io.plots.DrawableWriterFactory.getInstance().get("image/svg+xml")

    val buf = java.io.ByteArrayOutputStream()

    writer.write(this, buf, sizeX, sizeY)

    return MIME(writer.mimeType to buf.toString())
}
